import React, { useState, useContext } from "react";
import { useRouter } from "next/router";
import { SignUpLink } from "./signup";
import Head from "next/head";
import Link from "next/link";
import { IndexLink } from "./index";
import useFormValidation from '../src/components/Auth/useFormValidation'
import validateLogin from "../src/components/Auth/validateLogin";
import firebase from "../src/firebase";
import { Typography } from "@material-ui/core";
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: '25ch',
    },
  },
}));
const INITIAL_STATE = {
  email: "",
  password: ""
};


const SignIn = () => {
  const classes = useStyles();
  const router = useRouter();
  const {
    handleSubmit,
    handleBlur,
    handleChange,
    values,
    errors,
    isSubmitting
  } = useFormValidation(INITIAL_STATE, validateLogin, authenticateUser);
  const [firebaseError, setFirebaseError] = React.useState(null);

  async function authenticateUser() {
    const { name, email, password } = values;
    try {
      await firebase.login(email, password)
      router.push("/home");
    } catch (err) {
      console.error("Authentication Error", err);
      setFirebaseError(err.message);
    }
  }

  return (
    <div className={classes.root}>
      <Head>
        <meta charset="UTF-8"></meta>
        <meta
          name="viewport"
          content="width=device-width, initial-scale=0.55"
        ></meta>
        <title>Homepage</title>
      </Head>
      <main>
      <div className="title">
          <center>
            <Typography variant="h2" style={{color:"white"}}>Welcome to My Page!</Typography>
            <Typography variant="h3" style={{color:"white"}}>Log In!</Typography>
        </center></div>
        <br></br>
        <center>
          <img src="https://media3.giphy.com/media/U4FkC2VqpeNRHjTDQ5/200w.webp?cid=ecf05e47gsh02qtsj0ix5g3fgb7uzl26tladnfpapgp7ucrr&rid=200w.webp"></img>
        </center>
        <center>
          <form onSubmit={handleSubmit}>
            <div className="grid">
              <div className="card">
                <TextField
                id="outlined-helperText"
                  onChange={handleChange}
                  value={values.email}
                  type="email"
                  label="username"
                  placeholder="someone@example.com"
                  name="email"
                  onBlur={handleBlur}
                  variant="outlined"
                />
                
        {errors.email && <p className="error-text">{errors.email}</p>}
              <br></br>
              <br></br>
                <TextField
                id="outlined-helperText"
                  onChange={handleChange}
                  value={values.password}
                  type="password"
                  label="password"
                  placeholder="Password"
                  name="password"
                  onBlur={handleBlur}
                  variant="outlined"
                />
                {errors.password && <p className="error-text">{errors.password}</p>}
                {firebaseError && <p className="error-text">{firebaseError}</p>}
                <center>
                  <Button 
                  variant="contained"
                  color="primary"
                  style={{ marginTop: "20px", width: "200px" }}type="submit" disabled={isSubmitting} >Sign InðŸ“¤</Button>
                </center>
                <br></br>
                <br></br>
                <center>
                  <SignUpLink />
                </center>
                <br></br>
                <center>
                </center>
              </div>
            </div>
          </form>
        </center>
      </main>
      <footer>
        <Typography variant="h5">Procheta Bhattacharyya</Typography>
      </footer>
      <style jsx>{`
      img{
        width:450px;
        height:450px;
      }
     
        footer {
          color:white;
          width: 100%;
          height: 100px;
          border-top: 1px solid #eaeaea;
          display: flex;
          justify-content: center;
          align-items: center;
          
        }

        footer img {
          margin-left: 0.5rem;
        }

        footer a {
          display: flex;
          justify-content: center;
          align-items: center;
        }
        .grid {
          display: flex;
          align-items: center;
          justify-content: center;
          flex-wrap: wrap;

          max-width: 1000px;
          margin-top: 4rem;
        }

        .card {
          background-image:
            url(https://t4.ftcdn.net/jpg/03/35/44/49/240_F_335444972_UgKdQD7wlTgfywtzqfOVzJOHjFyma9bv.jpg);
          background-size: cover;
          background-position: top;
          position: relative; 
          margin: 2rem;
          flex-basis: 45%;
          padding: 3rem;
          text-align: left;
          color: inherit;
          text-decoration: none;
          border: 1px solid grey;
          border-radius: 20px;
          -moz-box-shadow: 5px 5px 5px 5px grey;
          -webkit-box-shadow: 5px 5px 5px 5px grey;
          box-shadow: 5px 5px 5px 5px grey;
        }


        .card h3 {
          margin: 0 0 1rem 0;
          font-size: 1.5rem;
        }

        .card p {
          margin: 0;
          font-size: 1.25rem;
          line-height: 1.5;
        }
        #l {
          text-align: center;
        }
        .logo {
          height: 1em;
        }
        .title {
          margin: 0;
          line-height: 1.15;
          font-size: 4rem;
          padding: 5rem 0;
        }
        #s {
          animation: pulse 5s infinite;
        }
        @keyframes pulse {
          20% {
            color: red;
          }
          30% {
            color: blue;
          }
          40% {
            color: green;
          }
          100% {
            color: yellow;
          }
        }
        #pro {
          color: blue;
        }
        form {
          font-size: 30px;
          padding: 0.0001rem 5em 5em 5em;
          margin: 100px 100px 100px 100px;
        }
        Button {
          align-items: center;
        }
        #new {
          text-align: center;
        }
        main{
          background-color:#153260;
        }
      `}</style>
      <style jsx global>{`
        html,
        body {
          padding: 0;
          margin: 0;
          font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto,
            Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
            sans-serif;
        }

        * {
          box-sizing: border-box;
        }
      `}</style>
    </div>
  );
};

export default SignIn;
